import cv2import numpy as npimg = cv2.imread(r"D:\python\loose_hands_detection\6.16\left-1080p\image\0009.jpg")def on_EVENT_LBUTTONDOWN(event, x, y, flags, param):    if event == cv2.EVENT_LBUTTONDOWN:        xy = "%d,%d" % (x, y)        cv2.circle(img, (x, y), 1, (255, 0, 0), thickness=-1)        cv2.putText(img, xy, (x, y), cv2.FONT_HERSHEY_PLAIN,                    4.0, (66, 89, 0), thickness=2)        cv2.imshow("image", img)cv2.namedWindow("image", cv2.WINDOW_KEEPRATIO)cv2.setMouseCallback("image", on_EVENT_LBUTTONDOWN)cv2.imshow("image", img)while (True):    try:        cv2.waitKey(20)    except Exception:        cv2.destroyWindow("image")        breakcv2.waitKey(10)cv2.destroyAllWindow()